import React, { useState } from 'react';
import './Style.css'; 
import img1 from '../data/1Mask group.png';
import img2 from '../data/2Mask group.png';
import img3 from '../data/3Mask group.png';
import img4 from '../data/4Mask group.png';
import img5 from '../data/5Mask group.png';

const ColumnAnimation = () => {
  const [activeImage, setActiveImage] = useState(img1); // State to track active image

  const handleImageHover = (image) => {
    setActiveImage(image);
  };

  return (
    <div className='ColumnAnimation'>
      {[img1, img2, img3, img4, img5].map((image, index) => (
        <div
          key={index}
          className='image-container'
          onMouseEnter={() => handleImageHover(image)}
        >
          <img src={image} alt={`Image ${index + 1}`} className='img' />
        </div>
      ))}
      <div className='active-image-container'>
        <img src={activeImage} alt='Active Image' className='active-img' />
      </div>
    </div>
  );
};

export default ColumnAnimation;
