import React, { useState, useRef, useEffect } from 'react';
import Logo from '../data/logo.png';
import ServiceVideo from '../data/About-us.mp4';
import img1 from '../data/1Mask group.png';
import img2 from '../data/2Mask group.png';
import img3 from '../data/3Mask group.png';
import img4 from '../data/4Mask group.png';
import img5 from '../data/5Mask group.png';
import Brand1 from '../data/Brands/Brand 0.png';
import Brand2 from '../data/Brands/pngwing 10.png';
import Brand3 from '../data/Brands/pngwing 2.png';
import Brand4 from '../data/Brands/pngwing 3.png';
import Brand5 from '../data/Brands/pngwing 4.png';
import Brand6 from '../data/Brands/pngwing 5.png';
import Brand7 from '../data/Brands/pngwing 6.png';
import Brand8 from '../data/Brands/pngwing 7.png';
import Brand9 from '../data/Brands/pngwing 8.png';
import Brand10 from '../data/Brands/pngwing 9.png';
import map from '../data/map.png';
import Location from '../data/Location.png';
import Animation from '../component/Animation';
import './Style.css';

const SimplePage = () => {
  const [hoveredIndex, setHoveredIndex] = useState(-1);
  const videoRef = useRef(null);
  const [activeImage, setActiveImage] = useState(img1);
  const [isAnimationStarted, setIsAnimationStarted] = useState(false);
  const imagePaths = [Brand1,Brand2,Brand3,Brand4,Brand5,Brand6,Brand7,Brand8,Brand9,Brand10];

  const columnRef = useRef(null);

  const handleMouseEnter1 = (image) => {
    setActiveImage(image);
  };

  const handleMouseEnter = (index) => {
    setHoveredIndex(index);
  };

  const handleMouseLeave = () => {
    setHoveredIndex(-1);
  };

  const imageItems = [
    { title: 'Neurodesign', description: 'Neurodesign uses neuroscience to create more engaging visual content by analyzing how people perceive and react to design elements.', image: img1 },
    { title: 'Neurobrand testing', description: 'Neurotesting enables precise assessment of packaging\'s competitive performance and forecasts impact', image: img2 },
    { title: 'Neuropackaging', description: 'Neuropackaging uses EEG to objectively measure consumer brain responses to optimize packaging design.', image: img3 },
    { title: 'User Experience', description: 'Benefits of UX and Usability analysis using biometric and eye tracking tools include measuring participants\' impulsive visual patterns.', image: img4 },
    { title: 'Product testing', description: 'NeuroX tests products using organoleptic techniques combined with biometric tools like BRI, EEGs, and face coding.', image: img5 },
  ];

  useEffect(() => {
    // Autoplay video on mount
    const video = videoRef.current;
    if (video) {
      video.play().catch(error => {
        console.error('Autoplay was prevented:', error);
        video.controls = true; // Fallback to showing controls
      });
    }

    // Start animation after a delay when the component mounts
    const observer = new IntersectionObserver(
      ([entry]) => {
        // When the column becomes visible
        if (entry.isIntersecting) {
          setIsAnimationStarted(true);
          observer.unobserve(entry.target); // Stop observing after animation starts
        }
      },
      { threshold: 0.5 } // Trigger when at least 50% of the target is visible
    );

    if (columnRef.current) {
      observer.observe(columnRef.current);
    }

    return () => {
      if (columnRef.current) {
        observer.unobserve(columnRef.current);
      }
    };
  }, []); 


  return (
    <section className="bg-black-100 p-10">
      <div className="container mx-auto flex">
        <img src={Logo} alt="logo" />
        <div className="flex ml-auto">
          <button className="lg:hidden">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6">
              <path strokeLinecap="round" strokeLinejoin="round" d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5" />
            </svg>
          </button>
          <ul className="hidden lg:flex my-auto">
            <li className="Navi-text flex text-black pr-5"><a href="">Services</a></li>
            <li className="Navi-text text-black pr-5"><a href="">Technology</a></li>
            <li className="Navi-text text-black pr-5"><a href="">Lab</a></li>
            <li className="Navi-text text-black pr-5"><a href="">Case Studies</a></li>
            <li className="Navi-text text-black pr-5"><a href="">Blog</a></li>
            <li className="Navi-text text-black pr-5"><a href="">Events/Resources</a></li>
            <li className="Navi-text text-black pr-5"><a href="">FAQs</a></li>
            <li className="Navi-text text-black pr-5"><a href="">About Us</a></li>
            <li className="Navi-text text-black pr-5">
              <a href="">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6">
                  <path strokeLinecap="round" strokeLinejoin="round" d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z" />
                </svg>
              </a>
            </li>
          </ul>
        </div>
      </div>

      <div className="lg:hidden router container text-customGrey mt-2">
        Home {'>'} <span className="text-customBlue pl-1"> Services </span> 
      </div>

      <div className="container mt-28 mx-auto">
        <div className="lg:text-8xl flex justify-center text-2xl">Services</div>
        <div className="mt-10 flex text-customGrey justify-center text-sm lg:text-center lg:mx-20">
          Lorem Ipsum is simply dummy text of the printing and typesetting industry. 
          Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, 
          when an unknown printer took a galley of type and scrambled it to make a type 
          specimen book. It has survived not only five centuries, but also 
        </div>
        <div className="container border mt-32 mx-auto video-container">
          <video ref={videoRef} className="h-38" autoPlay muted loop playsInline>
            <source src={ServiceVideo} type="video/mp4" />
          </video>
        </div>
      </div>
      <div className='hidden'>
      <Animation/>
      </div>
      <div className='container  mx-auto mt-32'>
        <div className='lg:mx-40 font-semibold' >Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen 
        <span className='text-customBlue'> book. It has survived not only five centuries, but also</span>  </div>
        <div className='text-center lg:mx-44 mt-8 text-customGrey'>Lorem Ipsum is simply dummy text of the printing 
          and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also </div>
      </div>
      <div ref={columnRef} className='container mt-16 mx-auto relative'></div>
      <div style={{ position: 'relative', width: '100%', height: 'auto' }}>
        {/* Map Image */}
        <img src={map} alt='map' style={{ width: '100%', height: 'auto' }} />

        {/* Location Image Positioned Over the Map */}
        <img src={Location} alt='location' className='location1'
          style={{ position: 'absolute',width:'2.5%',top: '50%',left: '70%',transform: 'translate(-50%, -50%)', }}/>
        <img src={Location} alt='location'  className={`location location2 ${isAnimationStarted ? 'animate-zoom-delayed-1' : ''}`}
          style={{position: 'absolute',width:'2.5%',top: '35%',left: '68%',transform: 'translate(-50%, -50%)', }}/>
        <img src={Location} alt='location' className={`location location3 ${isAnimationStarted ? 'animate-zoom-delayed-4' : ''}`}
         style={{position: 'absolute',width:'1.5%',top: '70%',left: '35%',transform: 'translate(-50%, -50%)', }}/>
        <img src={Location} alt='location' className={`location location4 ${isAnimationStarted ? 'animate-zoom-delayed-2' : ''}`}
          style={{position: 'absolute',width:'1.5%',top: '45%',left: '50%',transform: 'translate(-50%, -50%)', }}/>
        <img src={Location} alt='location' className='location5'
          style={{position: 'absolute',width:'1.5%',top: '40.5%',left: '72%',transform: 'translate(-50%, -50%)', }}/>
        <img src={Location} alt='location' className={`location location6 ${isAnimationStarted ? 'animate-zoom-delayed-3' : ''}`}
          style={{position: 'absolute',width:'1%',top: '35%',left: '65%',transform: 'translate(-50%, -50%)', }}/>
        <img src={Location} alt='location' className='location7'
          style={{position: 'absolute',width:'1%',top: '36%',left: '74%',transform: 'translate(-50%, -50%)', }}/>
        <img src={Location} alt='location' className={`location location8 ${isAnimationStarted ? 'animate-zoom-delayed-5' : ''}`}
          style={{position: 'absolute',width:'1%',top: '60%',left: '53%',transform: 'translate(-50%, -50%)', }}/> 
      </div>
      <div className='container flex  mt-44'>
      {/* Use a wrapping div for the scrolling animation */}
      <div className='Partners_page_brands_container flex'>
          {imagePaths.map((imagePath, index) => (
            <img key={index} src={imagePath} alt={`Brand ${index + 1}`} className='Partners_page_img' />
          ))}
        </div>
    </div>
    <div className=' container mx-auto mt-48 lg:flex bg-black'>
      <div className='w-full lg:w-1/2 px-20 lg:px-auto py-14 text-2xl lg:text-7xl text-white text-center lg:text-right'>Ready to work with us ?</div>
      <div className='w-full lg:w-1/2 px-8  py-14 flex items-center lg:bg-black  bg-white justify-center lg:justify-end'>
        <div className='w-auto h-auto flex px-12 py-6 bg-customBlue text-white rounded-full'>Get Started <span className='ml-4'><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
          <path stroke-linecap="round" stroke-linejoin="round" d="M13.5 4.5 21 12m0 0-7.5 7.5M21 12H3" /></svg></span> 
        </div>
      </div>
    </div>
    <div className='container text-black  mt-12 text-customGrey lg:mt-48'>
      <div className='flex border  flex-wrap'>
        <div className='w-2/4  ' >
          <img src={Logo} className=''  />
          <p className='text-3.5 '>NeuroX is revolutionizing the way businesses 
          understand consumer behavior by  merging cutting-edge neuroscience with marketing strategies. 
          Our team of experts  utilizes state-of-the-art technologies such as EEG, Facial Coding, and 
          Eye Tracking to  uncover deep insights into consumer preferences, emotions, and decision-making  
          processes. With our own state-of-the-art lab based in London and a team of passionate  professionals
          </p></div>
        <div className='w-1.5/4 ml-9'>
          <h1 className='semibold text-customBlue text-base'>Navigation</h1>
          <div className='text-sm flex flex-col '>
            <div className='mt-4 py-4'>Service</div>
            <div className='py-4'>Technology</div>
            <div className='py-4'>Case Study</div>
            <div className='py-4'>Resource</div>
            <div className='py-4'>Contact</div>
          </div>
          </div>
        <div className='w-1.5/4 ml-28 '>
        <h1 className='semibold text-customBlue text-base'>Licence</h1>
          <div className='text-sm flex flex-col '>
            <div className='mt-4 py-4'>Privacy Policy</div>
            <div className='py-4'>Copyright</div>
            <div className='py-4'>Email Address</div>
          </div>

        </div>
        <div className='w-1.5/4 '>
        <h1 className='semibold text-customBlue text-base'>Contact</h1>
          <div className='text-sm flex flex-col '>
            <div className='mt-4 py-4'><a href='tel:+44 7564 522241'>+44 7564 522241</a></div>
            <div className='py-4'><a href='mailto:contact@neurox.co.uk'><span><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
  <path stroke-linecap="round" stroke-linejoin="round" d="M21.75 6.75v10.5a2.25 2.25 0 0 1-2.25 2.25h-15a2.25 2.25 0 0 1-2.25-2.25V6.75m19.5 0A2.25 2.25 0 0 0 19.5 4.5h-15a2.25 2.25 0 0 0-2.25 2.25m19.5 0v.243a2.25 2.25 0 0 1-1.07 1.916l-7.5 4.615a2.25 2.25 0 0 1-2.36 0L3.32 8.91a2.25 2.25 0 0 1-1.07-1.916V6.75" />
</svg>
</span>contact@neurox.co.uk</a></div>
            <div className='py-4 flex'><span><svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
  <path stroke-linecap="round" stroke-linejoin="round" d="M15 10.5a3 3 0 1 1-6 0 3 3 0 0 1 6 0Z" />
  <path stroke-linecap="round" stroke-linejoin="round" d="M19.5 10.5c0 7.142-7.5 11.25-7.5 11.25S4.5 17.642 4.5 10.5a7.5 7.5 0 1 1 15 0Z" />
</svg>
</span>20 Wenlock Road, London, N1 7GU</div>
          </div>

        </div>
      </div>
    </div>

    </section>
  );
};

export default SimplePage;
