import React, { useState } from 'react';
import img1 from '../data/1Mask group.png';
import img2 from '../data/2Mask group.png';
import img3 from '../data/3Mask group.png';
import img4 from '../data/4Mask group.png';
import img5 from '../data/5Mask group.png';
import UpArrow from '../data/uparrow.png';
import DownArrow from '../data/Downarrow.png';
import './Style.css'; // Import CSS file for component-specific styles

const DataTable = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [isRotated, setIsRotated] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);

  const data = [
    {
      name: 'Neurodesign',
      paragraph: 'Neurodesign uses neuroscience to create more engaging visual content by analyzing how people perceive and react to design elements.',
      image: img1,
    },
    {
      name: 'Neurobrand Testing',
      paragraph: "Neurotesting enables precise assessment of packaging's competitive performance and forecasts impact",
      image: img2,
    },
    {
      name: 'Neuropackaging',
      paragraph: 'Neuropackaging uses EEG to objectively measure consumer brain responses to optimize packaging design.',
      image: img3,
    },
    {
      name: 'User Experience',
      paragraph: "Benefits of UX and Usability analysis using biometric and eye tracking tools include measuring participants' impulsive visual patterns.",
      image: img4,
    },
    {
      name: 'Product Testing',
      paragraph: 'NeuroX tests products using organoleptic techniques combined with biometric tools like BRI, EEGs, and face coding.',
      image: img5,
    },
    {
      name: 'Neuro Informed Development',
      paragraph: "Neuro Informed Development integrates neuroscience insights into product design, optimizing user experiences based on human cognitive processes and behaviors.",
      image: 'https://via.placeholder.com/150', // Placeholder URL (update as needed)
    },
  ];

  const handleHover = (index) => {
    setHoveredIndex(index);
    setIsRotated(true); // Trigger rotation animation
    setCurrentImage(data[index].image); // Set the current image to the hovered row's image
  };

  const handleMouseLeave = () => {
    setIsRotated(false); // Reset rotation state when mouse leaves
    // Do not reset currentImage here to keep the last hovered image displayed
  };

  // Set the first row's image as the default image if no row is hovered
  React.useEffect(() => {
    setCurrentImage(img1);
  }, []); // Run this effect only once on initial mount

  return (
    <div className="overflow-x-auto p-10">
      <table className="min-w-full" onMouseLeave={handleMouseLeave}>
        <tbody className="divide-y divide-gray-200">
          {data.map((item, index) => (
            <tr
              key={index}
              className=""
              onMouseEnter={() => handleHover(index)}
            >
              <td className="py-6 text-4xl whitespace-nowrap">{item.name}</td>
              <td className="py-6 text-xl max-w-lg break-words">{item.paragraph}</td>
              <td className="">
                {(hoveredIndex === null || hoveredIndex === index) && currentImage ? (
                  <img
                    src={currentImage}
                    alt={item.name}
                    style={{ height: '150px', width: '150px', objectFit: 'cover', marginBottom: 0 }}
                  />
                ) : null}
              </td>
              <td className="py-6">
                {/* Display arrow icon conditionally based on hover state */}
                <img
                  src={isRotated && hoveredIndex === index ? UpArrow : DownArrow}
                  alt="Arrow"
                  className={isRotated && hoveredIndex === index ? 'arrow-icon rotated' : 'arrow-icon'}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataTable;
