import React, { useRef, useEffect } from 'react';
import Logo from '../data/logo.png';
import ServiceVideo from '../data/About-us.mp4';
import img1 from '../data/1Mask group.png';
import img2 from '../data/2Mask group.png';
import img3 from '../data/3Mask group.png';
import img4 from '../data/4Mask group.png';
import img5 from '../data/5Mask group.png';
import './Style.css';


const SimplePage = () => {
  const videoRef = useRef(null);

  const handleImageHover = (imgContainer, index) => {
    const originalSrc = imgContainer.querySelector('img').src;
    imgContainer.addEventListener('mouseenter', () => {
      imgContainer.style.transform = 'translateY(50px)';
      setTimeout(() => {
        imgContainer.querySelector('img').src = `img${index + 2}.jpg`;
        imgContainer.style.transform = 'translateY(0)';
      }, 300);
    });

    imgContainer.addEventListener('mouseleave', () => {
