import React, { useState } from 'react';
import './Style.css'; // Import CSS file for component-specific styles

// Define an object to store image imports dynamically
const images = {
  img1: require('../data/1Mask').default,
  img2: require('../data/2Mask group.png').default,
  img3: require('../data/3Mask group.png').default,
  img4: require('../data/4Mask group.png').default,
  img5: require('../data/5Mask group.png').default,
  upArrow: require('../data/uparrow.png').default,
  downArrow: require('../data/Downarrow.png').default,
};

const DataTable = () => {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const [isRotated, setIsRotated] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);

  const data = [
    {
      name: 'Neurodesign',
      paragraph:
        'Neurodesign uses neuroscience to create more engaging visual content by analyzing how people perceive and react to design elements.',
      image: 'img1',
    },
    {
      name: 'Neurobrand Testing',
      paragraph: "Neurotesting enables precise assessment of packaging's competitive performance and forecasts impact",
      image: 'img2',
    },
    {
      name: 'Neuropackaging',
      paragraph:
        'Neuropackaging uses EEG to objectively measure consumer brain responses to optimize packaging design.',
      image: 'img3',
    },
    {
      name: 'User Experience',
      paragraph:
        "Benefits of UX and Usability analysis using biometric and eye tracking tools include measuring participants' impulsive visual patterns.",
      image: 'img4',
    },
    {
      name: 'Product Testing',
      paragraph:
        'NeuroX tests products using organoleptic techniques combined with biometric tools like BRI, EEGs, and face coding.',
      image: 'img5',
    },
    {
      name: 'Neuro Informed Development',
      paragraph:
        'Neuro Informed Development integrates neuroscience insights into product design, optimizing user experiences based on human cognitive processes and behaviors.',
      image: 'https://via.placeholder.com/150', // Placeholder URL (not in images object)
    },
  ];

  const handleHover = (index) => {
    setHoveredIndex(index);
    setIsRotated(true); // Trigger rotation animation
    setCurrentImage(data[index].image); // Set the current image to the hovered row's image
  };

  const handleMouseLeave = () => {
    setIsRotated(false); // Reset rotation state when mouse leaves
    setCurrentImage(null); // Reset current image
  };

  return (
    <div className="overflow-x-auto p-10">
      <table className="min-w-full">
        <tbody className="divide-y divide-gray-200">
          {data.map((item, index) => (
            <tr
              key={index}
              className=""
              onMouseEnter={() => handleHover(index)}
              onMouseLeave={handleMouseLeave}
            >
              <td className="py-6 text-4xl whitespace-nowrap">{item.name}</td>
              <td className="py-6 text-xl max-w-lg break-words">{item.paragraph}</td>
              <td className="">
                {index === 0 || hoveredIndex === index ? (
                  <img
                    src={currentImage ? images[currentImage] : images[item.image]}
                    alt={item.name}
                    style={{ height: '150px', width: '150px', objectFit: 'cover', marginBottom: 0 }}
                  />
                ) : null}
              </td>
              <td className="py-6">
                {/* Display arrow icon conditionally based on hover state */}
                <img
                  src={isRotated && hoveredIndex === index ? images.upArrow : images.downArrow}
                  alt="Arrow"
                  className={isRotated && hoveredIndex === index ? 'arrow-icon rotated' : 'arrow-icon'}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataTable;
