import React, { useRef, useEffect } from 'react';
import Logo from '../data/logo.png';
import ServiceVideo from '../data/About-us.mp4';
import img1 from '../data/1Mask group.png';
import img2 from '../data/2Mask group.png';
import img3 from '../data/3Mask group.png';
import img4 from '../data/4Mask group.png';
import img5 from '../data/5Mask group.png';
import './Style.css';


const SimplePage = () => {
  const imageRef = useRef(null);

  useEffect(() => {
    const handleMouseEnter = (index) => {
      const imgElement = imageRef.current;

      // Move the image down smoothly
      imgElement.style.transform = `translateY(50px)`;
      setTimeout(() => {
        // Change the image source after the transition
        imgElement.src = `img${index + 2}.jpg`; // Assuming img2, img3, etc.
        imgElement.style.transform = `translateY(0)`;
      }, 300); // Adjust timing (ms) based on transition duration
    };

    const handleMouseLeave = () => {
      const imgElement = imageRef.current;

      // Move the image up smoothly
      imgElement.style.transform = `translateY(-50px)`;
      setTimeout(() => {
        // Change the image source after the transition
        imgElement.src = `img1.jpg`; // Default image
